mapper.update:
update item
set item_name=#{updateParam.itemName},
    price=#{updateParam.price},
    quantity=#{updateParam.quantoty}
where id = #{id}

mapper.select:
select id, item_name, price, qantity from item
and item_name like concat('%', #{itemName}, '%')
and price $lt;= #{maxPrice}

select id, item_name, price, qantity
from item
